import React, { useState } from 'react'
import Products from './components/Products'
import Extras from './components/Extras'
import Shipping from './components/Shipping'
import Submit from './components/Submit'
import Fish from './img/fish.png'
import Shark from './img/shark.png'
function App() {
  const [plopperCount, setPlopperCount] = useState(0)
  function changePlopperCount(newPlopperCount) {
    console.log(newPlopperCount);
    setPlopperCount(newPlopperCount)
  }
  const [runningTotal, setRunningTotal] = useState(0)
  function changeRunningTotal(newRunningTotal) {
    setRunningTotal(prevRunningTotal => newRunningTotal)
  }
  const [currentPage, setCurrentPage] = useState('products');
  const Tabs = [
    { "name": changePagesProducts, "text": "Products" },
    { "name": changePagesExtra, "text": "Extras" },
    { "name": changePagesShipping, "text": "Shipping" },
    { "name": changePagesSubmit, "text": "Submit" }
  ]
  let page = () => {
    if (currentPage === 'products') {
      return (
        <Products
          changeRunningTotal={changeRunningTotal}
          runningTotal={runningTotal}
          setPlopperCount={setPlopperCount}
          plopperCount={plopperCount}
        />
      )
    } else if (currentPage === 'extras') {
      return (
        <Extras
          changeRunningTotal={changeRunningTotal}
          runningTotal={runningTotal}
        />
      )
    } else if (currentPage === 'shipping') {
      return (
        <Shipping />
      )
    } else if (currentPage === 'submit') {
      return (
        <Submit />
      )
    }
  }
  return (
    <div className="container-fluid bg-danger">
      <div className="row">
        <div className="col-3 text-center">
          <div className="row justify-content-center">
            <img src={Shark} className="img-fluid" />
          </div>
          <div class="btn-group-vertical">
            {Tabs.map((item, i) =>
              <button
                key={i}
                className="btn-block btn-primary my-4"
                onClick={() => setCurrentPage(item.text.toLowerCase())}>
                {item.text}
              </button>
            )}
          </div>
        </div>
        <div className="col-9 mb-4">
          {page}
        </div>
      </div>
      <div className="row justify-content-center">
        <img className="img" src={Fish} />
      </div>
    </div>
  )
}
export default App
