import React from 'react'

function Submit({
    grandTotal,
    extrasTotal,
    productTotal,
    shippingPrice,
    plopperCount,
    jigCount,
    spoonCount,
    biteMeCount,
    showBobsCount,
    beerCount
}) {
    const orders = [
        { "name": "Whopper Plopper", "count": plopperCount },
        { "name": "Spin Jig", "count": jigCount },
        { "name": "Jiggin' Spoon", "count": spoonCount },
        { "name": "Bite Me Shirt", "count": biteMeCount },
        { "name": "Show Your Bobbers Shirt", "count": showBobsCount },
        { "name": "Beer Opener", "count": beerCount }
    ]
    console.log(orders);
    return (
        <div>
            <h1 className="bg-success text-white text-center">üê†Submitüê†</h1>
            <ul className="list-group">
                {orders.map((item, i) => {
                    <li className="list-group-item"
                        key={i}
                        id={i}>
                        {item.count}
                        {item.name}
                    </li>
                })}
            </ul>
            <ul className="list-group list-group-flush">
                <li className="list-group-item text-right">Order Subtotal ${Number((productTotal + extrasTotal + shippingPrice)).toFixed(2)}</li>
                <li className="list-group-item text-right">KY Sales Tax ${Number(((productTotal + extrasTotal + shippingPrice) * .06)).toFixed(2)}</li>
                <li className="list-group-item text-right">Grand Total ${Number((grandTotal).toFixed(2))}</li>
            </ul>
        </div>
    )
}

export default Submit
